import "./App.css";
import Header from "./components/Header";
import Characters from "./components/Characters";
import React, { useEffect } from "react";

function App() {
  var dark = false;
  useEffect(() => {
    isDME(dark);
  }, [dark, isDME]);
  var isDME = (isEnabled) => {
    dark = isEnabled;
    console.log(dark);
  };
  return dark ? (
    <div className="darkMode">
      <h1>Hola mundo</h1>
      <Header isDarkModeEnabled={isDME} />
      <Characters />
    </div>
  ) : (
    <div className="lightMode">
      <h1>Hola mundo</h1>
      <Header isDarkModeEnabled={isDME} />
      <Characters />
    </div>
  );
}

export default App;
